import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import * as React from 'react';

interface ChartData {
  [key: string]: string | number;
}

interface ExampleState {
  data: ChartData[];
  loading: boolean;
  error: string | null;
}

export default function Example(): JSX.Element {
  const [state, setState] = React.useState<ExampleState>({
    data: [],
    loading: true,
    error: null
  });

  React.useEffect(() => {
    const loadCSVData = () => {
      fetch('/data.csv')
        .then((response) => {
          if (!response.ok) {
            throw new Error('Failed to fetch CSV file');
          }
          return response.text();
        })
        .then((csvText) => {
          // Simple CSV parser
          const lines = csvText.split('\n');
          const headers = lines[0].split(',').map((h: string) => h.trim());

          const parsedData: ChartData[] = [];
          for (let i = 1; i < lines.length; i++) {
            if (lines[i].trim()) {
              const values = lines[i].split(',');
              const row: ChartData = {};
              for (let j = 0; j < headers.length; j++) {
                const header = headers[j];
                const value = values[j] ? values[j].trim() : '';
                // Try to convert to number, otherwise keep as string
                const numValue = parseFloat(value);
                row[header] = !isNaN(numValue) ? numValue : value;
              }
              parsedData.push(row);
            }
          }

          setState({
            data: parsedData,
            loading: false,
            error: null
          });
        })
        .catch((err) => {
          setState({
            data: [],
            loading: false,
            error: 'Failed to load CSV data'
          });
          console.error('Error loading CSV:', err);
        });
    };

    loadCSVData();
  }, []);

  if (state.loading) {
    return <div>Loading CSV data...</div>;
  }

  if (state.error) {
    return <div>Error: {state.error}</div>;
  }

  if (state.data.length === 0) {
    return <div>No data available</div>;
  }

  // Get the first few keys for line chart (excluding name/label column)
  const allKeys = Object.keys(state.data[0]);
  const numericKeys: string[] = [];
  let nameKey = '';

  // Find numeric and string columns
  for (let i = 0; i < allKeys.length; i++) {
    const key = allKeys[i];
    const value = state.data[0][key];
    if (typeof value === 'number') {
      numericKeys.push(key);
    } else if (typeof value === 'string' && !nameKey) {
      nameKey = key;
    }
  }

  // Use first 2 numeric columns
  const dataKeys = numericKeys.slice(0, 2);

  // Use first string column or first column as name key
  if (!nameKey) {
    nameKey = allKeys[0];
  }

  return (
    <ResponsiveContainer width="100%" height="100%">
      <LineChart
        width={500}
        height={300}
        data={data}
        margin={{
          top: 5,
          right: 30,
          left: 20,
          bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />
        <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
      </LineChart>
    </ResponsiveContainer>
  );
}
