import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';


interface ChartData {
  [key: string]: string | number;
}

export default function Example() {
  const [data, setData] = useState<ChartData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadCSVData = async () => {
      try {
        // Option 1: Load CSV from public folder
        const response = await fetch('/data.csv');
        const csvText = await response.text();

        // Simple CSV parser
        const lines = csvText.split('\n');
        const headers = lines[0].split(',').map(h => h.trim());

        const parsedData: ChartData[] = [];
        for (let i = 1; i < lines.length; i++) {
          if (lines[i].trim()) {
            const values = lines[i].split(',');
            const row: ChartData = {};
            headers.forEach((header, index) => {
              const value = values[index]?.trim();
              // Try to convert to number, otherwise keep as string
              row[header] = !isNaN(Number(value)) ? Number(value) : value;
            });
            parsedData.push(row);
          }
        }

        setData(parsedData);
        setLoading(false);
      } catch (err) {
        setError('Failed to load CSV data');
        setLoading(false);
        console.error('Error loading CSV:', err);
      }
    };

    loadCSVData();
  }, []);

  if (loading) return <div>Loading CSV data...</div>;
  if (error) return <div>Error: {error}</div>;
  if (data.length === 0) return <div>No data available</div>;

  // Get the first few keys for line chart (excluding name/label column)
  const dataKeys = Object.keys(data[0]).filter(key => 
    typeof data[0][key] === 'number'
  ).slice(0, 2); // Take first 2 numeric columns

  const nameKey = Object.keys(data[0]).find(key => 
    typeof data[0][key] === 'string'
  ) || Object.keys(data[0])[0];

  return (
    <ResponsiveContainer width="100%" height="100%">
      <LineChart
        width={500}
        height={300}
        data={data}
        margin={{
          top: 5,
          right: 30,
          left: 20,
          bottom: 5,
        }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Line type="monotone" dataKey="pv" stroke="#8884d8" activeDot={{ r: 8 }} />
        <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
      </LineChart>
    </ResponsiveContainer>
  );
}
